mapper.select:
select id,pid,code,url.sort,active, (select max(name) from v_globalization_dictionary where mkey='ACCOUNTTYPE' and language=#{lang} and val=type) as type_name,(select max(name) from v_globalization_dictionary where mkey='LOGINTYPE' and language=#{lang} and val=logintype) as logintype_name,(select max(name) from v_globalization_dictionary where mkey='ACTIVETYPE' and language=#{lang} and val=active) as active_name from wf_processnode where 1=1
and active=${condition.active}
limit ${page.startR},${page.endR}
